import { useCallback, useEffect, useRef } from 'react'

const getElement = (): HTMLElement => {
  let element = document.getElementById('report-container')

  if (element === null) {
    element = document.createElement('div')
    element.id = 'report-container'
    element.style.position = 'fixed'
    element.style.top = '50%'
    element.style.left = '50%'
    element.style.fontSize = '32px'
    element.style.transform = 'translate(-50%, -50px)'
    element.style.padding = '20px'
    element.style.background = 'rgba(240, 240, 240, 0.4)'
    element.style.borderRadius = '20px'

    if (document.body) {
      document.body.appendChild(element)
    }
  }

  return element
}

export function useReport(): (arg0: string) => ReturnType<typeof setTimeout> {
  const timer = useRef<ReturnType<typeof setTimeout> | null>(null)
  const cleanup = useCallback(() => {
    if (timer.current !== null) {
      clearTimeout(timer.current)
      timer.current = null
    }

    if (document.body) {
      document.body.removeChild(getElement())
    }
  }, [])

  useEffect(() => {
    return cleanup
  }, [cleanup])

  return useCallback(
    (content) => {
      console.log(content)
      const element = getElement()
      if (timer.current !== null) {
        clearTimeout(timer.current)
      }
      element.innerHTML = content
      timer.current = setTimeout(cleanup, 1000)
      return timer.current
    },
    [cleanup]
  )
}

import { useEffect } from 'react'

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import {
  $getSelection,
  BaseSelection,
  COMMAND_PRIORITY_CRITICAL,
} from 'lexical'
import { SELECTION_CHANGE_COMMAND } from 'lexical'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'

export function useUpdateToolbarHandler(
  callback: (selection: BaseSelection) => void
) {
  const [editor] = useLexicalComposerContext()
  const { activeEditor } = useToolbarContext()

  useEffect(() => {
    return activeEditor.registerCommand(
      SELECTION_CHANGE_COMMAND,
      () => {
        const selection = $getSelection()
        if (selection) {
          callback(selection)
        }
        return false
      },
      COMMAND_PRIORITY_CRITICAL
    )
  }, [editor, callback])

  useEffect(() => {
    activeEditor.getEditorState().read(() => {
      const selection = $getSelection()
      if (selection) {
        callback(selection)
      }
    })
  }, [activeEditor, callback])
}

