import { useCallback } from 'react'

import { $isDecoratorBlockNode } from '@lexical/react/LexicalDecoratorBlockNode'
import { $isHeadingNode, $isQuoteNode } from '@lexical/rich-text'
import { $isTableSelection } from '@lexical/table'
import { $getNearestBlockElementAncestorOrThrow } from '@lexical/utils'
import {
  $createParagraphNode,
  $getSelection,
  $isRangeSelection,
  $isTextNode,
} from 'lexical'
import { EraserIcon } from 'lucide-react'

import { Button } from '@/registry/default/ui/button'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'

export function ClearFormattingToolbarPlugin() {
  const { activeEditor } = useToolbarContext()

  const clearFormatting = useCallback(() => {
    activeEditor.update(() => {
      const selection = $getSelection()
      if ($isRangeSelection(selection) || $isTableSelection(selection)) {
        const anchor = selection.anchor
        const focus = selection.focus
        const nodes = selection.getNodes()
        const extractedNodes = selection.extract()

        if (anchor.key === focus.key && anchor.offset === focus.offset) {
          return
        }

        nodes.forEach((node, idx) => {
          // We split the first and last node by the selection
          // So that we don't format unselected text inside those nodes
          if ($isTextNode(node)) {
            // Use a separate variable to ensure TS does not lose the refinement
            let textNode = node
            if (idx === 0 && anchor.offset !== 0) {
              textNode = textNode.splitText(anchor.offset)[1] || textNode
            }
            if (idx === nodes.length - 1) {
              textNode = textNode.splitText(focus.offset)[0] || textNode
            }
            /**
             * If the selected text has one format applied
             * selecting a portion of the text, could
             * clear the format to the wrong portion of the text.
             *
             * The cleared text is based on the length of the selected text.
             */
            // We need this in case the selected text only has one format
            const extractedTextNode = extractedNodes[0]
            if (nodes.length === 1 && $isTextNode(extractedTextNode)) {
              textNode = extractedTextNode
            }

            if (textNode.__style !== '') {
              textNode.setStyle('')
            }
            if (textNode.__format !== 0) {
              textNode.setFormat(0)
              $getNearestBlockElementAncestorOrThrow(textNode).setFormat('')
            }
            node = textNode
          } else if ($isHeadingNode(node) || $isQuoteNode(node)) {
            node.replace($createParagraphNode(), true)
          } else if ($isDecoratorBlockNode(node)) {
            node.setFormat('')
          }
        })
      }
    })
  }, [activeEditor])

  return (
    <Button
      size={'sm'}
      variant={'outline'}
      className="h-8 w-8"
      onClick={clearFormatting}
    >
      <EraserIcon className="h-4 w-4" />
    </Button>
  )
}

import { useCallback, useState } from 'react'

import {
  $isCodeNode,
  CODE_LANGUAGE_FRIENDLY_NAME_MAP,
  CODE_LANGUAGE_MAP,
  getLanguageFriendlyName,
} from '@lexical/code'
import { $isListNode } from '@lexical/list'
import { $findMatchingParent } from '@lexical/utils'
import {
  $getNodeByKey,
  $isRangeSelection,
  $isRootOrShadowRoot,
  BaseSelection,
} from 'lexical'

import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
} from '@/registry/default/ui/select'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/registry/default/editor/editor-hooks/use-update-toolbar'

function getCodeLanguageOptions(): [string, string][] {
  const options: [string, string][] = []

  for (const [lang, friendlyName] of Object.entries(
    CODE_LANGUAGE_FRIENDLY_NAME_MAP
  )) {
    options.push([lang, friendlyName])
  }

  return options
}

const CODE_LANGUAGE_OPTIONS = getCodeLanguageOptions()

export function CodeLanguageToolbarPlugin() {
  const { activeEditor } = useToolbarContext()
  const [codeLanguage, setCodeLanguage] = useState<string>('')
  const [selectedElementKey, setSelectedElementKey] = useState<string | null>(
    null
  )

  const $updateToolbar = (selection: BaseSelection) => {
    if ($isRangeSelection(selection)) {
      const anchorNode = selection.anchor.getNode()
      let element =
        anchorNode.getKey() === 'root'
          ? anchorNode
          : $findMatchingParent(anchorNode, (e) => {
              const parent = e.getParent()
              return parent !== null && $isRootOrShadowRoot(parent)
            })

      if (element === null) {
        element = anchorNode.getTopLevelElementOrThrow()
      }

      const elementKey = element.getKey()
      const elementDOM = activeEditor.getElementByKey(elementKey)

      if (elementDOM !== null) {
        setSelectedElementKey(elementKey)

        if (!$isListNode(element) && $isCodeNode(element)) {
          const language =
            element.getLanguage() as keyof typeof CODE_LANGUAGE_MAP
          setCodeLanguage(
            language ? CODE_LANGUAGE_MAP[language] || language : ''
          )
          return
        }
      }
    }
  }

  useUpdateToolbarHandler($updateToolbar)

  const onCodeLanguageSelect = useCallback(
    (value: string) => {
      activeEditor.update(() => {
        if (selectedElementKey !== null) {
          const node = $getNodeByKey(selectedElementKey)
          if ($isCodeNode(node)) {
            node.setLanguage(value)
          }
        }
      })
    },
    [activeEditor, selectedElementKey]
  )

  return (
    <Select>
      <SelectTrigger className="h-8 w-min gap-1">
        <span>{getLanguageFriendlyName(codeLanguage)}</span>
      </SelectTrigger>
      <SelectContent>
        {CODE_LANGUAGE_OPTIONS.map(([value, label]) => (
          <SelectItem
            key={value}
            value={value}
            onPointerUp={() => {
              onCodeLanguageSelect(value)
            }}
          >
            {label}
          </SelectItem>
        ))}
      </SelectContent>
    </Select>
  )
}

import { useState } from 'react'

import { $isLinkNode } from '@lexical/link'
import { $findMatchingParent } from '@lexical/utils'
import {
  $isElementNode,
  $isRangeSelection,
  BaseSelection,
  ElementFormatType,
  FORMAT_ELEMENT_COMMAND,
  INDENT_CONTENT_COMMAND,
  OUTDENT_CONTENT_COMMAND,
} from 'lexical'
import { IndentIncreaseIcon } from 'lucide-react'
import { IndentDecreaseIcon } from 'lucide-react'
import { AlignLeftIcon } from 'lucide-react'
import { AlignJustifyIcon } from 'lucide-react'
import { AlignCenterIcon, AlignRightIcon } from 'lucide-react'

import { Separator } from '@/registry/default/ui/separator'
import { ToggleGroup, ToggleGroupItem } from '@/registry/default/ui/toggle-group'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/registry/default/editor/editor-hooks/use-update-toolbar'
import { getSelectedNode } from '@/registry/default/editor/utils/get-selected-node'

const ELEMENT_FORMAT_OPTIONS: {
  [key in Exclude<ElementFormatType, 'start' | 'end' | ''>]: {
    icon: React.ReactNode
    iconRTL: string
    name: string
  }
} = {
  left: {
    icon: <AlignLeftIcon className="size-4" />,
    iconRTL: 'left-align',
    name: 'Left Align',
  },
  center: {
    icon: <AlignCenterIcon className="size-4" />,
    iconRTL: 'center-align',
    name: 'Center Align',
  },
  right: {
    icon: <AlignRightIcon className="size-4" />,
    iconRTL: 'right-align',
    name: 'Right Align',
  },
  justify: {
    icon: <AlignJustifyIcon className="size-4" />,
    iconRTL: 'justify-align',
    name: 'Justify Align',
  },
} as const

export function ElementFormatToolbarPlugin() {
  const { activeEditor } = useToolbarContext()
  const [elementFormat, setElementFormat] = useState<ElementFormatType>('left')

  const $updateToolbar = (selection: BaseSelection) => {
    if ($isRangeSelection(selection)) {
      const node = getSelectedNode(selection)
      const parent = node.getParent()

      let matchingParent
      if ($isLinkNode(parent)) {
        // If node is a link, we need to fetch the parent paragraph node to set format
        matchingParent = $findMatchingParent(
          node,
          (parentNode) => $isElementNode(parentNode) && !parentNode.isInline()
        )
      }
      setElementFormat(
        $isElementNode(matchingParent)
          ? matchingParent.getFormatType()
          : $isElementNode(node)
            ? node.getFormatType()
            : parent?.getFormatType() || 'left'
      )
    }
  }

  useUpdateToolbarHandler($updateToolbar)

  const handleValueChange = (value: string) => {
    if (!value) return // Prevent unselecting current value

    setElementFormat(value as ElementFormatType)

    if (value === 'indent') {
      activeEditor.dispatchCommand(INDENT_CONTENT_COMMAND, undefined)
    } else if (value === 'outdent') {
      activeEditor.dispatchCommand(OUTDENT_CONTENT_COMMAND, undefined)
    } else {
      activeEditor.dispatchCommand(
        FORMAT_ELEMENT_COMMAND,
        value as ElementFormatType
      )
    }
  }

  return (
    <ToggleGroup
      type="single"
      value={elementFormat}
      defaultValue={elementFormat}
      onValueChange={handleValueChange}
      className="flex gap-1"
    >
      {/* Alignment toggles */}
      {Object.entries(ELEMENT_FORMAT_OPTIONS).map(([value, option]) => (
        <ToggleGroupItem
          key={value}
          value={value}
          variant={'outline'}
          aria-label={option.name}
          size="sm"
          className="h-8 w-8 p-0"
        >
          {option.icon}
        </ToggleGroupItem>
      ))}

      <Separator orientation="vertical" className="h-8" />
      {/* Indentation toggles */}
      <ToggleGroupItem
        value="outdent"
        aria-label="Outdent"
        variant={'outline'}
        size="sm"
        className="h-8 w-8"
      >
        <IndentDecreaseIcon className="size-4" />
      </ToggleGroupItem>

      <ToggleGroupItem
        value="indent"
        variant={'outline'}
        aria-label="Indent"
        size="sm"
        className="h-8 w-8"
      >
        <IndentIncreaseIcon className="size-4" />
      </ToggleGroupItem>
    </ToggleGroup>
  )
}

