import { useCallback, useState } from 'react'

import {
  $getSelectionStyleValueForProperty,
  $patchStyleText,
} from '@lexical/selection'
import { $getSelection, $isRangeSelection, BaseSelection } from 'lexical'
import { Minus, Plus } from 'lucide-react'

import { Button } from '@/registry/default/ui/button'
import { Input } from '@/registry/default/ui/input'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/registry/default/editor/editor-hooks/use-update-toolbar'

const DEFAULT_FONT_SIZE = 16
const MIN_FONT_SIZE = 1
const MAX_FONT_SIZE = 72

export function FontSizeToolbarPlugin() {
  const style = 'font-size'
  const [fontSize, setFontSize] = useState(DEFAULT_FONT_SIZE)

  const { activeEditor } = useToolbarContext()

  const $updateToolbar = (selection: BaseSelection) => {
    if ($isRangeSelection(selection)) {
      const value = $getSelectionStyleValueForProperty(
        selection,
        'font-size',
        `${DEFAULT_FONT_SIZE}px`
      )
      setFontSize(parseInt(value) || DEFAULT_FONT_SIZE)
    }
  }

  useUpdateToolbarHandler($updateToolbar)

  const updateFontSize = useCallback(
    (newSize: number) => {
      const size = Math.min(Math.max(newSize, MIN_FONT_SIZE), MAX_FONT_SIZE)
      activeEditor.update(() => {
        const selection = $getSelection()
        if (selection !== null) {
          $patchStyleText(selection, {
            [style]: `${size}px`,
          })
        }
      })
      setFontSize(size)
    },
    [activeEditor, style]
  )

  return (
    <div className="flex items-center gap-1">
      <div className="flex items-center gap-1">
        <Button
          variant="outline"
          size="icon"
          className="h-8 w-8"
          onClick={() => updateFontSize(fontSize - 1)}
          disabled={fontSize <= MIN_FONT_SIZE}
        >
          <Minus className="size-3" />
        </Button>
        <Input
          value={fontSize}
          onChange={(e) =>
            updateFontSize(parseInt(e.target.value) || DEFAULT_FONT_SIZE)
          }
          className="h-8 w-12 text-center"
          min={MIN_FONT_SIZE}
          max={MAX_FONT_SIZE}
        />
        <Button
          variant="outline"
          size="icon"
          className="h-8 w-8"
          onClick={() => updateFontSize(fontSize + 1)}
          disabled={fontSize >= MAX_FONT_SIZE}
        >
          <Plus className="size-3" />
        </Button>
      </div>
    </div>
  )
}

import { useEffect, useImperativeHandle, useState } from 'react'

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import { mergeRegister } from '@lexical/utils'
import {
  CAN_REDO_COMMAND,
  CAN_UNDO_COMMAND,
  COMMAND_PRIORITY_CRITICAL,
  LexicalEditor,
  REDO_COMMAND,
  UNDO_COMMAND,
} from 'lexical'
import { RedoIcon, UndoIcon } from 'lucide-react'

import { Button } from '@/registry/default/ui/button'

import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'

const IS_APPLE = true

export function HistoryToolbarPlugin() {
  const [editor] = useLexicalComposerContext()
  const { activeEditor, $updateToolbar } = useToolbarContext()
  const [isEditable, setIsEditable] = useState(editor.isEditable())
  const [canUndo, setCanUndo] = useState(false)
  const [canRedo, setCanRedo] = useState(false)

  useEffect(() => {
    return mergeRegister(
      editor.registerEditableListener((editable) => {
        setIsEditable(editable)
      }),
      activeEditor.registerUpdateListener(({ editorState }) => {
        editorState.read(() => {
          $updateToolbar()
        })
      }),
      activeEditor.registerCommand<boolean>(
        CAN_UNDO_COMMAND,
        (payload) => {
          setCanUndo(payload)
          return false
        },
        COMMAND_PRIORITY_CRITICAL
      ),
      activeEditor.registerCommand<boolean>(
        CAN_REDO_COMMAND,
        (payload) => {
          setCanRedo(payload)
          return false
        },
        COMMAND_PRIORITY_CRITICAL
      )
    )
  }, [$updateToolbar, activeEditor, editor])

  return (
    <div className="flex items-center gap-1">
      <Button
        disabled={!canUndo || !isEditable}
        onClick={() => {
          activeEditor.dispatchCommand(UNDO_COMMAND, undefined)
        }}
        title={IS_APPLE ? 'Undo (Z)' : 'Undo (Ctrl+Z)'}
        type="button"
        aria-label="Undo"
        size="sm"
        className="h-8 w-8"
        variant={'outline'}
      >
        <UndoIcon className="size-4" />
      </Button>
      <Button
        disabled={!canRedo || !isEditable}
        onClick={() => {
          activeEditor.dispatchCommand(REDO_COMMAND, undefined)
        }}
        title={IS_APPLE ? 'Redo (Z)' : 'Redo (Ctrl+Y)'}
        type="button"
        aria-label="Redo"
        size="sm"
        className="h-8 w-8"
        variant={'outline'}
      >
        <RedoIcon className="size-4" />
      </Button>
    </div>
  )
}

import { useCallback, useEffect, useState } from 'react'

import { $isLinkNode, TOGGLE_LINK_COMMAND } from '@lexical/link'
import {
  $isRangeSelection,
  BaseSelection,
  COMMAND_PRIORITY_NORMAL,
} from 'lexical'
import { KEY_MODIFIER_COMMAND } from 'lexical'
import { LinkIcon } from 'lucide-react'

import { Toggle } from '@/registry/default/ui/toggle'

import { useFloatingLinkContext } from '@/registry/default/editor/context/floating-link-context'
import { useToolbarContext } from '@/registry/default/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/registry/default/editor/editor-hooks/use-update-toolbar'
import { getSelectedNode } from '@/registry/default/editor/utils/get-selected-node'
import { sanitizeUrl } from '@/registry/default/editor/utils/url'

export function LinkToolbarPlugin() {
  const { activeEditor } = useToolbarContext()
  const { setIsLinkEditMode } = useFloatingLinkContext()
  const [isLink, setIsLink] = useState(false)

  const $updateToolbar = (selection: BaseSelection) => {
    if ($isRangeSelection(selection)) {
      const node = getSelectedNode(selection)
      const parent = node.getParent()
      if ($isLinkNode(parent) || $isLinkNode(node)) {
        setIsLink(true)
      } else {
        setIsLink(false)
      }
    }
  }

  useUpdateToolbarHandler($updateToolbar)

  useEffect(() => {
    return activeEditor.registerCommand(
      KEY_MODIFIER_COMMAND,
      (payload) => {
        const event: KeyboardEvent = payload
        const { code, ctrlKey, metaKey } = event

        if (code === 'KeyK' && (ctrlKey || metaKey)) {
          event.preventDefault()
          let url: string | null
          if (!isLink) {
            setIsLinkEditMode(true)
            url = sanitizeUrl('https://')
          } else {
            setIsLinkEditMode(false)
            url = null
          }
          return activeEditor.dispatchCommand(TOGGLE_LINK_COMMAND, url)
        }
        return false
      },
      COMMAND_PRIORITY_NORMAL
    )
  }, [activeEditor, isLink, setIsLinkEditMode])

  const insertLink = useCallback(() => {
    if (!isLink) {
      setIsLinkEditMode(true)
      activeEditor.dispatchCommand(TOGGLE_LINK_COMMAND, sanitizeUrl('https://'))
    } else {
      setIsLinkEditMode(false)
      activeEditor.dispatchCommand(TOGGLE_LINK_COMMAND, null)
    }
  }, [activeEditor, isLink, setIsLinkEditMode])

  return (
    <Toggle
      size={'sm'}
      variant={'outline'}
      aria-label="Toggle link"
      onClick={insertLink}
    >
      <LinkIcon className="h-4 w-4" />
    </Toggle>
  )
}

import { useState } from 'react'

import { $isTableSelection } from '@lexical/table'
import { $isRangeSelection, BaseSelection, FORMAT_TEXT_COMMAND } from 'lexical'
