      excalidrawNode.setData(
        JSON.stringify({
          appState,
          elements,
          files,
        })
      )
      $insertNodes([excalidrawNode])
      if ($isRootOrShadowRoot(excalidrawNode.getParentOrThrow())) {
        $wrapNodeInElement(excalidrawNode, $createParagraphNode).selectEnd()
      }
