  getTableObserverFromTableElement,
} from '@lexical/table'
import type { ElementNode, LexicalEditor } from 'lexical'
import {
  $createParagraphNode,
  $getRoot,
  $getSelection,
  $isElementNode,
  $isParagraphNode,
  $isRangeSelection,
  $isTextNode,
} from 'lexical'
import { ChevronDownIcon, PaintBucketIcon } from 'lucide-react'
